var server = current.windows_server;
var action = current.action;
var params = current.parameters;

if (server && action) {
	var ecc = new GlideRecord("ecc_queue");  
	ecc.initialize();  
	ecc.agent = "mid.server.mid1";  
	ecc.topic = "Command";        
	ecc.name = "powershell c:\\scripts\\doit.ps1 -Server " + server + " -Action " + action + " -Params " + params;

	ecc.queue = "output";  
	ecc.state = "ready"; 

	workflow.scratchpad.ecc_sysid = ecc.insert();	
	
}

